INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text_en`,`text_fr`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES
(@ENTRY,0,0,"This war - and don't mistake this for anythign but war - is not going to win itself. If we do not fight, scratch, claw, and kick for every speck of land on this shattered world, all will be lost.","<Traduction manquante>",12,0,100,0,0,0,"Marshal Isildor"),
