INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text_en`,`text_fr`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES
(@ENTRY,0,0,"Of course, my friend. How can I be of service to you?","<Traduction manquante>",12,0,100,0,0,0,"Anchorite Nindumen"),
(@ENTRY,1,0,"A noble request. It would be an honor, Erothem.","<Traduction manquante>",12,0,100,0,0,0,"Anchorite Nindumen"),
(@ENTRY,2,0,"My friends, please join me in humble supplication to the Light.","<Traduction manquante>",12,0,100,0,0,0,"Anchorite Nindumen"),
(@ENTRY,3,0,"Light that we embrace, we beseech thee...","<Traduction manquante>",12,0,100,0,0,0,"Anchorite Nindumen"),
(@ENTRY,4,0,"Into our struggle against the darkness we have sent our children.","<Traduction manquante>",12,0,100,0,0,0,"Anchorite Nindumen"),
(@ENTRY,5,0,"Please bless and protect them, and grant them success over those who seek to do us harm...","<Traduction manquante>",12,0,100,0,0,0,"Anchorite Nindumen"),
(@ENTRY,6,0,"Continue to bless this, our ancient home, we beg.","<Traduction manquante>",12,0,100,0,0,0,"Anchorite Nindumen"),
(@ENTRY,7,0,"Bless us with tolerance for our elven visitors.","<Traduction manquante>",12,0,100,0,0,0,"Anchorite Nindumen"),
(@ENTRY,8,0,"By tha Naaru, may it be so.","<Traduction manquante>",12,0,100,0,0,0,"Anchorite Nindumen");
